	void CommGraspCore::to_ostream(std::ostream &os) const
	{
	  os << "CommGrasp(";
	  os << getId() << " ";
	  getPose().to_ostream(os);
	  os << getQuality() << " ";
	  os << getWidth() << " ";
	  os << getValid() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommGraspCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<id>" << getId() << "</id>";
		os << indent << "<pose>";
		getPose().to_xml(os, indent);
		os << indent << "</pose>";
		os << indent << "<quality>" << getQuality() << "</quality>";
		os << indent << "<width>" << getWidth() << "</width>";
		os << indent << "<valid>" << getValid() << "</valid>";
	}
	
	// restore from xml stream
	void CommGraspCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_id("<id>");
		static const Smart::KnuthMorrisPratt kmp_pose("<pose>");
		static const Smart::KnuthMorrisPratt kmp_quality("<quality>");
		static const Smart::KnuthMorrisPratt kmp_width("<width>");
		static const Smart::KnuthMorrisPratt kmp_valid("<valid>");
		
		if(kmp_id.search(is)) {
			std::string idItem;
			is >> idItem;
			setId(idItem);
		}
		if(kmp_pose.search(is)) {
			CommBasicObjects::CommPose3d poseItem;
			poseItem.from_xml(is);
			setPose(poseItem);
		}
		if(kmp_quality.search(is)) {
			double qualityItem;
			is >> qualityItem;
			setQuality(qualityItem);
		}
		if(kmp_width.search(is)) {
			double widthItem;
			is >> widthItem;
			setWidth(widthItem);
		}
		if(kmp_valid.search(is)) {
			bool validItem;
			is >> validItem;
			setValid(validItem);
		}
	}
	
