	void CommVacummGripperStateCore::to_ostream(std::ostream &os) const
	{
	  os << "CommVacummGripperState(";
	  getState().to_ostream(os);
	  os << getVacuumLevel() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommVacummGripperStateCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<state>";
		getState().to_xml(os, indent);
		os << indent << "</state>";
		os << indent << "<vacuumLevel>" << getVacuumLevel() << "</vacuumLevel>";
	}
	
	// restore from xml stream
	void CommVacummGripperStateCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_state("<state>");
		static const Smart::KnuthMorrisPratt kmp_vacuumLevel("<vacuumLevel>");
		
		if(kmp_state.search(is)) {
			CommManipulatorObjects::VacuumGraspState stateItem;
			stateItem.from_xml(is);
			setState(stateItem);
		}
		if(kmp_vacuumLevel.search(is)) {
			double vacuumLevelItem;
			is >> vacuumLevelItem;
			setVacuumLevel(vacuumLevelItem);
		}
	}
	
