	void CommImageObjectRecognitionResultCore::to_ostream(std::ostream &os) const
	{
	  os << "CommImageObjectRecognitionResult(";
	  os << getXmin() << " ";
	  os << getYmin() << " ";
	  os << getXmax() << " ";
	  os << getYmax() << " ";
	  getBelief().to_ostream(os);
	  os << getObjectId() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommImageObjectRecognitionResultCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<xmin>" << getXmin() << "</xmin>";
		os << indent << "<ymin>" << getYmin() << "</ymin>";
		os << indent << "<xmax>" << getXmax() << "</xmax>";
		os << indent << "<ymax>" << getYmax() << "</ymax>";
		os << indent << "<belief>";
		getBelief().to_xml(os, indent);
		os << indent << "</belief>";
		os << indent << "<objectId>" << getObjectId() << "</objectId>";
	}
	
	// restore from xml stream
	void CommImageObjectRecognitionResultCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_xmin("<xmin>");
		static const Smart::KnuthMorrisPratt kmp_ymin("<ymin>");
		static const Smart::KnuthMorrisPratt kmp_xmax("<xmax>");
		static const Smart::KnuthMorrisPratt kmp_ymax("<ymax>");
		static const Smart::KnuthMorrisPratt kmp_belief("<belief>");
		static const Smart::KnuthMorrisPratt kmp_objectId("<objectId>");
		
		if(kmp_xmin.search(is)) {
			unsigned int xminItem;
			is >> xminItem;
			setXmin(xminItem);
		}
		if(kmp_ymin.search(is)) {
			unsigned int yminItem;
			is >> yminItem;
			setYmin(yminItem);
		}
		if(kmp_xmax.search(is)) {
			unsigned int xmaxItem;
			is >> xmaxItem;
			setXmax(xmaxItem);
		}
		if(kmp_ymax.search(is)) {
			unsigned int ymaxItem;
			is >> ymaxItem;
			setYmax(ymaxItem);
		}
		if(kmp_belief.search(is)) {
			CommObjectRecognitionObjects::CommObjectBelief beliefItem;
			beliefItem.from_xml(is);
			setBelief(beliefItem);
		}
		if(kmp_objectId.search(is)) {
			unsigned int objectIdItem;
			is >> objectIdItem;
			setObjectId(objectIdItem);
		}
	}
	
