	const char* CommPrintDataCore::getCompiledHash()
	{
		return DomainPrintIDL::REPO_HASH;
	}
	
	void CommPrintDataCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void CommPrintDataCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommPrintDataCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t CommPrintDataCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, std::string(data.data.c_str()));
		boost::hash_combine(seed, data.format);
		
		return seed;
	}
	#endif
	
	// default constructor
	CommPrintDataCore::CommPrintDataCore()
	:	idl_CommPrintData()
	{  
		setData("");
		setFormat(DomainPrint::PrintDataEnum());
	}
	
	CommPrintDataCore::CommPrintDataCore(const DATATYPE &data)
	:	idl_CommPrintData(data)
	{  }

	CommPrintDataCore::~CommPrintDataCore()
	{  }
	
	void CommPrintDataCore::to_ostream(std::ostream &os) const
	{
	  os << "CommPrintData(";
	  os << getData() << " ";
	  getFormat().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void CommPrintDataCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<data>" << getData() << "</data>";
		os << indent << "<format>";
		getFormat().to_xml(os, indent);
		os << indent << "</format>";
	}
	
	// restore from xml stream
	void CommPrintDataCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_data("<data>");
		static const Smart::KnuthMorrisPratt kmp_format("<format>");
		
		if(kmp_data.search(is)) {
			std::string dataItem;
			is >> dataItem;
			setData(dataItem);
		}
		if(kmp_format.search(is)) {
			DomainPrint::PrintDataEnum formatItem;
			formatItem.from_xml(is);
			setFormat(formatItem);
		}
	}
	
