	void CommPathCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommPathCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for DomainRobotFleetNavigation::CommNode(idl_CommPath.node)
		DomainRobotFleetNavigation::CommNode::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t CommPathCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.id);
		boost::hash_combine(seed, data.direction);
		boost::hash_combine(seed, data.width);
		std::vector<DomainRobotFleetNavigationIDL::CommNode>::const_iterator data_nodeIt;
		for(data_nodeIt=data.node.begin(); data_nodeIt!=data.node.end(); data_nodeIt++) {
			seed += DomainRobotFleetNavigation::CommNode::generateDataHash(*data_nodeIt);
		}
		
		return seed;
	}
	#endif
	
