	void CommPathCore::to_ostream(std::ostream &os) const
	{
	  os << "CommPath(";
	  os << getId() << " ";
	  os << getDirection() << " ";
	  os << getWidth() << " ";
	  std::vector<DomainRobotFleetNavigation::CommNode>::const_iterator nodeIt;
	  std::vector<DomainRobotFleetNavigation::CommNode> nodeList = getNodeCopy();
	  for(nodeIt=nodeList.begin(); nodeIt!=nodeList.end(); nodeIt++) {
	  	nodeIt->to_ostream(os);
	  }
	  os << ") ";
	}
	
	// convert to xml stream
	void CommPathCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		os << indent << "<id>" << getId() << "</id>";
		os << indent << "<direction>" << getDirection() << "</direction>";
		os << indent << "<width>" << getWidth() << "</width>";
		std::vector<DomainRobotFleetNavigation::CommNode>::const_iterator nodeIt;
		std::vector<DomainRobotFleetNavigation::CommNode> nodeList = getNodeCopy();
		counter = 0;
		os << indent << "<nodeList n=\"" << nodeList.size() << "\">";
		for(nodeIt=nodeList.begin(); nodeIt!=nodeList.end(); nodeIt++) {
			os << indent << "<node i=\"" << counter++ << "\">";
			nodeIt->to_xml(os, indent);
			os << indent << "</node>";
		}
		os << indent << "</nodeList>";
	}
	
	// restore from xml stream
	void CommPathCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_id("<id>");
		static const Smart::KnuthMorrisPratt kmp_direction("<direction>");
		static const Smart::KnuthMorrisPratt kmp_width("<width>");
		static const Smart::KnuthMorrisPratt kmp_nodeList("<nodeList n=\"");
		static const Smart::KnuthMorrisPratt kmp_node("\">");
		
		if(kmp_id.search(is)) {
			int idItem;
			is >> idItem;
			setId(idItem);
		}
		if(kmp_direction.search(is)) {
			int directionItem;
			is >> directionItem;
			setDirection(directionItem);
		}
		if(kmp_width.search(is)) {
			double widthItem;
			is >> widthItem;
			setWidth(widthItem);
		}
		if(kmp_nodeList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			DomainRobotFleetNavigation::CommNode nodeItem;
			std::vector<DomainRobotFleetNavigation::CommNode> nodeList;
			kmp_node.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_node.search(is)) {
					nodeItem.from_xml(is);
					nodeList.push_back(nodeItem);
				}
			}
			setNode(nodeList);
		}
	}
	
