	const char* CommImageRegionOfInterestCore::getCompiledHash()
	{
		return DomainVisionIDL::REPO_HASH;
	}
	
	void CommImageRegionOfInterestCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void CommImageRegionOfInterestCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommImageRegionOfInterestCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t CommImageRegionOfInterestCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.x_offset);
		boost::hash_combine(seed, data.y_offset);
		boost::hash_combine(seed, data.height);
		boost::hash_combine(seed, data.width);
		boost::hash_combine(seed, data.do_rectify);
		
		return seed;
	}
	#endif
	
