	void CommImageRegionOfInterestCore::to_ostream(std::ostream &os) const
	{
	  os << "CommImageRegionOfInterest(";
	  os << getX_offset() << " ";
	  os << getY_offset() << " ";
	  os << getHeight() << " ";
	  os << getWidth() << " ";
	  os << getDo_rectify() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommImageRegionOfInterestCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<x_offset>" << getX_offset() << "</x_offset>";
		os << indent << "<y_offset>" << getY_offset() << "</y_offset>";
		os << indent << "<height>" << getHeight() << "</height>";
		os << indent << "<width>" << getWidth() << "</width>";
		os << indent << "<do_rectify>" << getDo_rectify() << "</do_rectify>";
	}
	
	// restore from xml stream
	void CommImageRegionOfInterestCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_x_offset("<x_offset>");
		static const Smart::KnuthMorrisPratt kmp_y_offset("<y_offset>");
		static const Smart::KnuthMorrisPratt kmp_height("<height>");
		static const Smart::KnuthMorrisPratt kmp_width("<width>");
		static const Smart::KnuthMorrisPratt kmp_do_rectify("<do_rectify>");
		
		if(kmp_x_offset.search(is)) {
			unsigned int x_offsetItem;
			is >> x_offsetItem;
			setX_offset(x_offsetItem);
		}
		if(kmp_y_offset.search(is)) {
			unsigned int y_offsetItem;
			is >> y_offsetItem;
			setY_offset(y_offsetItem);
		}
		if(kmp_height.search(is)) {
			unsigned int heightItem;
			is >> heightItem;
			setHeight(heightItem);
		}
		if(kmp_width.search(is)) {
			unsigned int widthItem;
			is >> widthItem;
			setWidth(widthItem);
		}
		if(kmp_do_rectify.search(is)) {
			bool do_rectifyItem;
			is >> do_rectifyItem;
			setDo_rectify(do_rectifyItem);
		}
	}
	
